import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import NoProfile from '../assets/imgae/no-profile.png'
import Torahack from '../assets/imgae/torahack.png'

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    maxWidth: '36ch',
    backgroundColor: theme.palette.background.paper,
  },
  inline: {
    display: 'inline',
  },
}));


const Chat = (props)=>{

  console.log(props.type)

  const isQuesiton = (props.typeã€€=== "question");

  console.log(isQuesiton);
  const classes = isQuesiton ? 'p-chat__row' : 'p-chat__reverse';

  return(
    <ListItem className = {classes}>
      <ListItemAvatar>
        {isQuesiton ? ( 
          <Avatar alt="icon" src={Torahack}/>
        ):( 
            <Avatar alt="icon" src={NoProfile} />
        )}
      </ListItemAvatar>
      <div className="p-chat__bubble">
        {props.chat}
        </div>
    </ListItem>
  )
}

export default Chat
